import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import avatar from '../assets/profile.png';
import toast, { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import { passwordValidate } from '../helper/validate';
import useFetch from '../hooks/fetch.hook';
import { useAuthStore } from '../store/store';
import { verifyPassword } from '../helper/helper';
import styles from '../styles/Username.module.css';

export default function Password() {
  const navigate = useNavigate();
  const { username } = useAuthStore((state) => state.auth);
  const [{ isLoading, apiData, serverError }] = useFetch(`/user/${username}`);

  const formik = useFormik({
    initialValues: {
      password: '',
    },
    validate: passwordValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      try {
        let loginPromise = verifyPassword({ username, password: values.password });

        toast.promise(loginPromise, {
          loading: 'Checking...',
          success: <b>Login Successfully...!</b>,
          error: <b>Password Not Match!</b>,
        });

        const res = await loginPromise;
        const { token, username, profile, type } = res.data;

        // Check if user is admin
        if (type === 'admin') {
          toast.error('You are an admin. Please log in through the admin portal.');
          return;
        }

        // Store user data
        localStorage.setItem('token', token);
        localStorage.setItem('username', username);
        localStorage.setItem('profile', profile);

        // Broadcast login event to sync other apps
        window.postMessage({ type: 'LOGIN', username, profile }, 'http://localhost:3000');
        window.postMessage({ type: 'LOGIN', username, profile }, 'http://localhost:3002');
        window.postMessage({ type: 'LOGIN', username, profile }, 'http://localhost:3004');

        // Trigger storage event for same-origin apps
        window.dispatchEvent(new Event('storage'));

        // Redirect to e-commerce home
        navigate(`/?username=${username}`);
      } catch (error) {
        console.error('Login failed:', error);
        toast.error('Login failed. Please try again.');
      }
    },
  });

  if (isLoading) return <h1 className="text-2xl font-bold">Loading...</h1>;
  if (serverError) return <h1 className="text-xl text-red-500">{serverError.message}</h1>;

  return (
    <div className="container mx-auto">
      <Toaster position="top-center" reverseOrder={false} />

      <div className="flex justify-center items-center h-screen">
        <div className={styles.glass}>
          <div className="title flex flex-col items-center">
            <h4 className="text-5xl font-bold">
              Hello {apiData?.firstName || apiData?.username}
            </h4>
            <span className="py-4 text-xl w-2/3 text-center text-gray-500">
              Explore More by connecting with us.
            </span>
          </div>

          <form className="py-1" onSubmit={formik.handleSubmit}>
            <div className="profile flex justify-center py-4">
              <img
                src={apiData?.profile || avatar}
                className={styles.profile_img}
                alt="avatar"
              />
            </div>

            <div className="textbox flex flex-col items-center gap-6">
              <input
                {...formik.getFieldProps('password')}
                className={styles.textbox}
                type="password"
                placeholder="Password"
              />
              <button className={styles.btn} type="submit">
                Sign In
              </button>
            </div>

            <div className="text-center py-4">
              <span className="text-gray-500">
                Forgot Password?{' '}
                <Link className="text-red-500" to="/recovery">
                  Recover Now
                </Link>
              </span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
