import React, { useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import avatar from '../assets/profile.png';
import { Toaster, toast } from 'react-hot-toast';
import { useFormik } from 'formik';
import { usernameValidate } from '../helper/validate'
import { useAuthStore } from '../store/store'
import axios from 'axios';

import styles from '../styles/Username.module.css';

export default function Username() {

  const navigate = useNavigate();
  const setUsername = useAuthStore(state => state.setUsername);

  const formik = useFormik({
    initialValues: {
      username: 'example123'
    },
    validate: usernameValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async values => {
      try {
        const { username } = values;

        // Make a request to the backend to get user info
        const { data } = await axios.get(`/api/user/${username}`);

        // Check if the user is admin
        if (data.type === 'admin') {
          toast.error("Access Denied. Please login through the Admin portal.");
          return; // Stop navigation
        }

        // Valid user, proceed to password page
        setUsername(username);
        navigate('/password');

      } catch (error) {
        if (error.response?.status === 404) {
          toast.error("User not found.");
        } else {
          toast.error("Something went wrong.");
        }
      }
    }
  });

  return (
    <div className="container mx-auto">

      <Toaster position='top-center' reverseOrder={false}></Toaster>

      <div className='flex justify-center items-center h-screen'>
        <div className={styles.glass}>

          <div className="title flex flex-col items-center">
            <h4 className='text-5xl font-bold'>Hello Again!</h4>
            <span className='py-4 text-xl w-2/3 text-center text-black-500'>
              Welcome to POLYMART.
            </span>
          </div>

          <form className='py-1' onSubmit={formik.handleSubmit}>
            <div className='profile flex justify-center py-4'>
              <img src={avatar} className={styles.profile_img} alt="avatar" />
            </div>

            <div className="textbox flex flex-col items-center gap-6">
              <input {...formik.getFieldProps('username')} className={styles.textbox} type="text" placeholder='Username' />
              <button className={styles.btn} type='submit'>Let's Go</button>
            </div>

            <div className="text-center py-4">
              <span className='text-black-500'>Not a Member <Link className='text-red-500' to="/register">Register Now</Link></span>
            </div>

            <div className="text-center py-4">
              <span className='text-black'>Are you admin? <Link className='text-red-500' to="/admin">Login</Link></span>
            </div>

          </form>

        </div>
      </div>
    </div>
  )
}
